extern volatile uint32_t TimeSecs;

uint16_t LastIndexH = 0;
uint16_t LastIndexM = 0;
uint16_t LastIndexS = 0;

void Draw_Analog(void){
  ST7735_DrawBitmap(25, 90, clock, 80, 80);
}

void Refresh_Anlgclk(void){
	uint32_t time = TimeSecs;
  uint16_t hrs = time/3600;
  uint16_t mins = (time%3600)/60;
  uint16_t secs = time%60;
  uint16_t meridiem = 0;
  if(hrs >= 12){
    meridiem = 15;
    hrs -= 12;
  }
  
  uint16_t indexS = secs;
  if(LastIndexS != indexS){
    ST7735_Line(65, 50, Xcoord[LastIndexS] + 25, Ycoord[LastIndexS] + 10, ST7735_BLACK);
  }
  ST7735_Line(65, 50, Xcoord[indexS] + 25, Ycoord[indexS] + 10, ST7735_RED);
  LastIndexS = indexS;
  
  uint16_t indexM = mins;
  if(LastIndexM != indexM){
    ST7735_Line(65, 50, Xcoord[LastIndexM] + 25, Ycoord[LastIndexM] + 10, ST7735_BLACK);
  }
  ST7735_Line(65, 50, Xcoord[indexM] + 25, Ycoord[indexM] + 10, ST7735_GREEN);
  LastIndexM = indexM;
  
  uint16_t indexH = hrs*5 + mins/15;
  if(LastIndexH != indexH){
    ST7735_Line(65, 50, Xcoord[LastIndexH] + 25, Ycoord[LastIndexH] + 10, ST7735_BLACK);
  }
  ST7735_Line(65, 50, Xcoord[indexH] + 25, Ycoord[indexH] + 10, ST7735_CYAN);
  LastIndexH = indexH;
  
  ST7735_SetCursor(17, 8);
  ST7735_OutChar('a' + meridiem);
  ST7735_OutChar('m');
}

